formulaMap.jsonSchema:
"{
  "type": "object",
  "properties": {
    "fullName": {
      "type": "string",
      "description": "The prospect's full name"
    },
    "currentJobTitle": {
      "type": "string",
      "description": "Current job title from their LinkedIn profile"
    },
    "location": {
      "type": "string",
      "description": "Location listed on their LinkedIn profile"
    },
    "backgroundSummary": {
      "type": "string",
      "description": "One sentence summarizing their professional background"
    }
  },
  "required": [
    "fullName",
    "currentJobTitle",
    "location",
    "backgroundSummary"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "companyDescription": {
      "type": "string",
      "description": "1-2 sentence description of what the company does"
    },
    "industryVertical": {
      "type": "string",
      "description": "Primary industry vertical"
    },
    "isHiring": {
      "type": "string",
      "description": "Whether the company appears to be actively hiring",
      "enum": [
        "Yes",
        "No",
        "Unknown"
      ]
    },
    "recentNews": {
      "type": [
        "string",
        "null"
      ],
      "description": "Any recent news or funding from the last 6 months, or null if nothing found"
    }
  },
  "required": [
    "companyDescription",
    "industryVertical",
    "isHiring",
    "recentNews"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "tier": {
      "type": "string",
      "description": "The qualification tier for this prospect",
      "enum": [
        "1",
        "2",
        "DQ"
      ]
    },
    "reasoning": {
      "type": "string",
      "description": "One sentence explaining the scoring decision"
    },
    "buyingPersona": {
      "type": "string",
      "description": "The buying persona category",
      "enum": [
        "Founder",
        "C-Level",
        "VP/Director",
        "Manager",
        "Other"
      ]
    }
  },
  "required": [
    "tier",
    "reasoning",
    "buyingPersona"
  ],
  "additionalProperties": false
}"
