formulaMap.jsonSchema:
"{
  "type": "object",
  "properties": {
    "targetRegionData": {
      "type": "object",
      "properties": {
        "regionName": {
          "type": "string",
          "description": "The name of the target region."
        },
        "hasPresence": {
          "type": "boolean",
          "description": "Whether the company has an employee presence in the target region."
        },
        "employeeCount": {
          "type": "integer",
          "description": "The number of employees in the target region as calculated by the hierarchy rule."
        },
        "percentageOfTotal": {
          "type": "number",
          "description": "The employee percentage of the target region over the total or max count in the Location JSON."
        }
      },
      "required": [
        "regionName",
        "hasPresence",
        "employeeCount",
        "percentageOfTotal"
      ],
      "additionalProperties": false
    },
    "secondaryRegionData": {
      "type": "object",
      "properties": {
        "regionName": {
          "type": "string",
          "description": "The name of the secondary region."
        },
        "hasPresence": {
          "type": "boolean",
          "description": "Whether the company has an employee presence in the secondary region."
        },
        "employeeCount": {
          "type": "integer",
          "description": "The number of employees in the secondary region as calculated by the hierarchy rule."
        },
        "percentageOfTotal": {
          "type": "number",
          "description": "The employee percentage of the secondary region over the total or max count in the Location JSON."
        }
      },
      "required": [
        "regionName",
        "hasPresence",
        "employeeCount",
        "percentageOfTotal"
      ],
      "additionalProperties": false
    },
    "geographicStructure": {
      "type": "object",
      "properties": {
        "isMultiLocation": {
          "type": "boolean",
          "description": "Whether the company is distributed with multiple significant locations."
        },
        "otherHubsDetected": {
          "type": "array",
          "description": "List of other top non-target locations where a significant number of employees are present.",
          "items": {
            "type": "string"
          }
        }
      },
      "required": [
        "isMultiLocation",
        "otherHubsDetected"
      ],
      "additionalProperties": false
    },
    "densityWinner": {
      "type": "object",
      "properties": {
        "locationName": {
          "type": "string",
          "description": "The name of the specific City or Metro Area with the highest employee count."
        },
        "employeeCount": {
          "type": "integer",
          "description": "The number of employees in the location with the highest count."
        },
        "matchesListedAddress": {
          "type": "boolean",
          "description": "Indicates whether the company address matches the location with the highest employee count."
        }
      },
      "required": [
        "locationName",
        "employeeCount",
        "matchesListedAddress"
      ],
      "additionalProperties": false
    },
    "sourceData": {
      "type": "object",
      "properties": {
        "methodUsed": {
          "type": "string",
          "description": "Indicates whether the Region Aggregate or Sum of Cities method was used."
        },
        "rawCountFound": {
          "type": "integer",
          "description": "The specific number used from the raw Location JSON data."
        },
        "totalEmployeesDenominator": {
          "type": "integer",
          "description": "The count used as the denominator for percentage calculations, typically the max count in the data."
        }
      },
      "required": [
        "methodUsed",
        "rawCountFound",
        "totalEmployeesDenominator"
      ],
      "additionalProperties": false
    },
    "reasoning": {
      "type": "string",
      "description": "Explanation of how the counts were derived according to the rules."
    }
  },
  "required": [
    "targetRegionData",
    "secondaryRegionData",
    "geographicStructure",
    "densityWinner",
    "sourceData",
    "reasoning"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "newsFound": {
      "type": "boolean",
      "description": "True if relevant employee-related news was found, otherwise false"
    },
    "companyNews": {
      "type": "object",
      "properties": {
        "searchPerformedAt": {
          "type": "string",
          "description": "The date the search was performed in YYYY-MM-DD format"
        },
        "employeeRelatedNews": {
          "type": "array",
          "description": "A list of news items related to employee satisfaction signals from leadership statements or workplace awards",
          "items": {
            "type": "object",
            "properties": {
              "sourceType": {
                "type": "string",
                "description": "The source type for the news item",
                "enum": [
                  "Award",
                  "Leadership Statement",
                  "Blog Post"
                ]
              },
              "whoSaidIt": {
                "type": "string",
                "description": "The name and title of the leader or name of the award"
              },
              "summary": {
                "type": "string",
                "description": "A 1-2 sentence summary of what was said or what award was given"
              },
              "specificValueOrPolicy": {
                "type": "string",
                "description": "The specific culture topic, value, or policy discussed"
              },
              "date": {
                "type": "string",
                "description": "The date of the news item in YYYY-MM-DD format"
              },
              "url": {
                "type": "string",
                "description": "The full URL to the source"
              }
            },
            "required": [
              "sourceType",
              "whoSaidIt",
              "summary",
              "specificValueOrPolicy",
              "date",
              "url"
            ],
            "additionalProperties": false
          }
        },
        "reasoning": {
          "type": "string",
          "description": "Reason for including these news items or for absence of relevant news"
        }
      },
      "required": [
        "searchPerformedAt",
        "employeeRelatedNews",
        "reasoning"
      ],
      "additionalProperties": false
    }
  },
  "required": [
    "newsFound",
    "companyNews"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "hrInvestmentScore": {
      "type": "object",
      "properties": {
        "totalScore": {
          "type": "integer",
          "description": "The combined HR investment score based on benefits and leadership signals (0-10)"
        },
        "part1BenefitsScore": {
          "type": "integer",
          "description": "Part 1 score from benefits analysis (0-5)"
        },
        "part2LeadershipScore": {
          "type": "integer",
          "description": "Score for leadership signals about internal management philosophy. Valid values: 0, 2, or 5"
        },
        "flagNoBenefits": {
          "type": "boolean",
          "description": "True if no benefits or perks were found in benefitsList"
        },
        "scoreReasoning": {
          "type": "string",
          "description": "One sentence explaining how the total score was determined"
        }
      },
      "required": [
        "totalScore",
        "part1BenefitsScore",
        "part2LeadershipScore",
        "flagNoBenefits",
        "scoreReasoning"
      ],
      "additionalProperties": false
    },
    "part2Analysis": {
      "type": "object",
      "properties": {
        "newsEvaluated": {
          "type": "integer",
          "description": "Number of news items analyzed to determine leadership signal"
        },
        "highestSignalType": {
          "type": "string",
          "description": "The strongest category of leadership signal detected: None, External Validation, or Internal Manifesto"
        },
        "evidence": {
          "type": "string",
          "description": "Quote or summary of the strongest leadership signal found, or 'No relevant signals'"
        }
      },
      "required": [
        "newsEvaluated",
        "highestSignalType",
        "evidence"
      ],
      "additionalProperties": false
    },
    "linksToSources": {
      "type": "array",
      "description": "URLs for the sources evaluated from cultureNews",
      "items": {
        "type": "string"
      }
    }
  },
  "required": [
    "hrInvestmentScore",
    "part2Analysis",
    "linksToSources"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "companyVertical": {
      "type": "string",
      "description": "The selected value for company vertical based on HubSpot property values."
    },
    "country": {
      "type": "string",
      "description": "The country name where the company is located."
    },
    "city": {
      "type": "string",
      "description": "The city name where the company is located."
    },
    "state": {
      "type": "string",
      "description": "The state name where the company is located."
    },
    "industry": {
      "type": "string",
      "description": "The selected ENUM value for industry based on HubSpot property values."
    },
    "officeLocation": {
      "type": "string",
      "description": "The mapped office location values from the multi-picker, joined with semicolons."
    },
    "lastFundingType": {
      "type": "string",
      "description": "The selected value for the last funding type."
    },
    "foundingDate": {
      "type": [
        "integer",
        "null"
      ],
      "description": "The founding date of the company as a Unix timestamp in milliseconds. Return null if the date is not found."
    },
    "sfEmployeeCount": {
      "type": [
        "integer",
        "null"
      ],
      "description": "Integer representing employee count in San Francisco Bay Area, or null if not available."
    },
    "nyEmployeeCount": {
      "type": [
        "integer",
        "null"
      ],
      "description": "Integer representing employee count in New York City Metropolitan Area, or null if not available."
    },
    "cultureResearchSummary": {
      "type": "string",
      "description": "A brief 1-2 sentence summary of company culture written for personalization."
    },
    "cultureNewsLinks": {
      "type": "string",
      "description": "URLs separated by semicolons for reference to relevant company culture/news."
    },
    "notableBenefits": {
      "type": "string",
      "description": "String listing only premium or ultra-premium benefits, or 'None' if none are found."
    }
  },
  "required": [
    "companyVertical",
    "country",
    "city",
    "state",
    "industry",
    "officeLocation",
    "lastFundingType",
    "foundingDate",
    "sfEmployeeCount",
    "nyEmployeeCount",
    "cultureResearchSummary",
    "cultureNewsLinks",
    "notableBenefits"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "scrapedAt": {
      "type": "string",
      "description": "The date when the data was scraped in YYYY-MM-DD format."
    },
    "cultureArtifacts": {
      "type": "array",
      "description": "List of raw snippets about company culture, values, and team vibe.",
      "items": {
        "type": "string"
      }
    },
