"{
  "type": "object",
  "properties": {
    "companyName": {
      "type": "string",
      "description": "The name of the company being analyzed"
    },
    "analysisDate": {
      "type": "string",
      "description": "The date the analysis was performed in YYYY-MM-DD format"
    },
    "signals": {
      "type": "array",
      "description": "A list of qualified high-quality sales signals",
      "items": {
        "type": "object",
        "properties": {
          "signalTier": {
            "type": "string",
            "description": "The priority tier of the signal, either TIER 1 or TIER 2",
            "enum": [
              "TIER 1",
              "TIER 2"
            ]
          },
          "category": {
            "type": "string",
            "description": "The category of the signal, such as 'AI Initiative' or 'Leadership Change'"
          },
          "headline": {
            "type": "string",
            "description": "A concise one-sentence summary of the finding"
          },
          "description": {
            "type": "string",
            "description": "A detailed explanation of why this signal matters and how it connects to the value proposition"
          },
          "evidenceQuote": {
            "type": "string",
            "description": "A direct quote from the news or report if available"
          },
          "sourceUrl": {
            "type": "string",
            "description": "A direct link to the article or press release where the signal was found"
          },
          "date": {
            "type": "string",
            "description": "The date of the signal in YYYY-MM-DD format"
          }
        },
        "required": [
          "signalTier",
          "category",
          "headline",
          "description",
          "evidenceQuote",
          "sourceUrl",
          "date"
        ],
        "additionalProperties": false
      }
    }
  },
  "required": [
    "companyName",
    "analysisDate",
    "signals"
  ],
  "additionalProperties": false
}"

