"{
  "type": "object",
  "properties": {
    "company": {
      "type": "string",
      "description": "The name of the company being analyzed"
    },
    "segment": {
      "type": "string",
      "description": "The chosen Pain-Qualified Segment for the company based on the provided classification logic",
      "enum": [
        "Compliance",
        "Catalog",
        "Experience",
        "Manual_Review"
      ]
    },
    "reasoning": {
      "type": "string",
      "description": "A brief explanation of why this segment was chosen for the company"
    },
    "confidenceScore": {
      "type": "string",
      "description": "The confidence score in the accuracy of the segment classification",
      "enum": [
        "High",
        "Medium",
        "Low"
      ]
    }
  },
  "required": [
    "company",
    "segment",
    "reasoning",
    "confidenceScore"
  ],
  "additionalProperties": false
}"

