formulaMap.jsonSchema:
"{
  "type": "object",
  "properties": {
    "fullName": {
      "type": "string",
      "description": "The full name of the individual being analyzed."
    },
    "title": {
      "type": "string",
      "description": "The current job title of the individual."
    },
    "company": {
      "type": "string",
      "description": "The current company where the individual is employed."
    },
    "analysis": {
      "type": "object",
      "properties": {
        "matrixPersona": {
          "type": "string",
          "description": "The matrix persona classification determined by department and altitude.",
          "enum": [
            "Strategic Marketing (C-Level/VP)",
            "Operational Marketing (Director/Head)",
            "Strategic Technical (C-Level/VP)",
            "Operational Technical (Director/Manager)",
            "Strategic MarTech (C-Level/VP)",
            "Operational MarTech (Director/Manager)"
          ]
        },
        "priorityTier": {
          "type": "string",
          "description": "The priority tier (P1, P2, P3, or DQ for disqualified) assigned to the individual.",
          "enum": [
            "P1",
            "P2",
            "P3",
            "DQ"
          ]
        },
        "priorityScore": {
          "type": "integer",
          "description": "The priority score assigned to the individual on a scale from 0 to 10."
        },
        "rationale": {
          "type": "string",
          "description": "A detailed explanation for the assigned priority tier and score, referencing specific criteria from the campaign."
        }
      },
      "required": [
        "matrixPersona",
        "priorityTier",
        "priorityScore",
        "rationale"
      ],
      "additionalProperties": false
    }
  },
  "required": [
    "fullName",
    "title",
    "company",
    "analysis"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "johnNormalized": {
      "type": "string",
      "description": "The normalized first name based on the provided {{firstName}}, processed according to strict capitalization, punctuation removal, and validation rules for use in professional email copy."
    }
  },
  "required": [
    "normalized_first_name"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "referralSentenceInmail": {
      "type": "string",
      "description": "The InMail version of the final closing sentence as generated per the specified logic and colleague count scenarios."
    },
    "referralSentenceEmail": {
      "type": "string",
      "description": "The email version of the final closing sentence as generated per the specified logic and colleague count scenarios."
    },
    "referralPeople": {
      "type": "string",
      "description": "The cleaned name(s) of selected valid colleague(s). If none are found, output 'N/A'."
    }
  },
  "required": [
    "referralSentenceInmail",
    "referralSentenceEmail",
    "referralPeople"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "validationSentence": {
      "type": "string",
      "description": "The complete P.S. Validation Sentence that links the authority and business outcome for the target, formatted as: 'I figured you are the right person for this since {{observation}}.'"
    }
  },
  "required": [
    "validationSentence"
  ],
  "additionalProperties": false
}"
