formulaMap.jsonSchema:
"{
  "type": "object",
  "properties": {
    "profileAnalysis": {
      "type": "object",
      "properties": {
        "score": {
          "type": "string",
          "description": "Tier score assigned to the profile: 1 for Primary, 2 for Secondary, 3 for Influencers, DQ for Disqualified.",
          "enum": [
            "1",
            "2",
            "3",
            "DQ"
          ]
        },
        "buyingPersona": {
          "type": "string",
          "description": "The persona type best matching the profile: Founder, C-lvl, People/HR Leader, or Other.",
          "enum": [
            "Founder",
            "C-lvl",
            "People/HR Leader",
            "Other"
          ]
        },
        "disqualificationReason": {
          "type": [
            "string",
            "null"
          ],
          "description": "If DQ, the reason: 'Location Mismatch' or 'Role Mismatch'. If qualified, return null."
        },
        "reasoning": {
          "type": "string",
          "description": "One sentence explaining the verdict based on Location or Bio signals."
        },
        "city": {
          "type": "string",
          "description": "The city from the prospect's LinkedIn location."
        },
        "country": {
          "type": "string",
          "description": "The country from the prospect's LinkedIn location."
        },
        "firstName": {
          "type": "string",
          "description": "The prospect's first name."
        },
        "lastName": {
          "type": "string",
          "description": "The prospect's last name."
        }
      },
      "required": [
        "score",
        "buyingPersona",
        "disqualificationReason",
        "reasoning",
        "city",
        "country",
        "firstName",
        "lastName"
      ],
      "additionalProperties": false
    }
  },
  "required": [
    "profileAnalysis"
  ],
  "additionalProperties": false
}"

