formulaMap.jsonSchema:
"{
  "type": "object",
  "properties": {
    "fullName": {
      "type": "string",
      "description": "Original full name string from the LinkedIn profile"
    },
    "firstName": {
      "type": "string",
      "description": "Normalized first name suitable for an email greeting"
    },
    "title": {
      "type": "string",
      "description": "Job title of the individual"
    },
    "company": {
      "type": "string",
      "description": "Company name where the individual is currently employed"
    },
    "analysis": {
      "type": "object",
      "properties": {
        "matrixPersona": {
          "type": "string",
          "description": "One of the 6 defined matrix personas based on department and altitude",
          "enum": [
            "Strategic Marketing",
            "Operational Marketing",
            "Strategic Technical",
            "Operational Technical",
            "Strategic MarTech",
            "Operational MarTech"
          ]
        },
        "priorityTier": {
          "type": "string",
          "description": "Priority tier for outreach: P1, P2, P3, or DQ",
          "enum": [
            "P1",
            "P2",
            "P3",
            "DQ"
          ]
        },
        "priorityScore": {
          "type": "integer",
          "description": "Priority score from 0 to 10 based on fit"
        },
        "rationale": {
          "type": "string",
          "description": "Explanation of the priority tier assignment based strictly on P1, P2, or P3 criteria"
        },
        "teamClassification": {
          "type": "string",
          "description": "Functional team classification based on matrix persona",
          "enum": [
            "Business",
            "Technical"
          ]
        }
      },
      "required": [
        "matrixPersona",
        "priorityTier",
        "priorityScore",
        "rationale",
        "teamClassification"
      ],
      "additionalProperties": false
    }
  },
  "required": [
    "fullName",
    "firstName",
    "title",
    "company",
    "analysis"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "whatCompanies": {
      "type": "string",
      "description": "A two-word lowercased plural category describing the company type, derived from the company overview, following the format [Adjective/Category] + [Plural Noun]."
    },
    "companyClean": {
      "type": "string",
      "description": "A colloquial, normalized version of the company name for casual conversation, with legal suffixes removed and limited to two words, except for famous three-word brands."
    },
    "space": {
      "type": "string",
      "description": "The lowercased broad industry sector followed by the word 'space', e.g., 'insurance space'."
    }
  },
  "required": [
    "whatCompanies",
    "companyClean",
    "space"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "similarRoles": {
      "type": "string",
      "description": "A conversational, pluralized group name for the prospect's peer role, normalized and simplified."
    },
    "team": {
      "type": "string",
      "description": "The functional area and organizational scope, formatted as '[Function] [Scope]'."
    }
  },
  "required": [
    "similarRoles",
    "team"
  ],
  "additionalProperties": false
}"

