"{
  "type": "object",
  "properties": {
    "verdict": {
      "type": "string",
      "description": "Classification of the company as Safe to contact or Likely competitor based on their business model.",
      "enum": [
        "Safe to contact",
        "Likely competitor"
      ]
    },
    "reasoning": {
      "type": "string",
      "description": "A one-sentence explanation of the verdict based on the company's business model."
    }
  },
  "required": [
    "verdict",
    "reasoning"
  ],
  "additionalProperties": false
}"
