"{
  "type": "object",
  "properties": {
    "lastFundingType": {
      "type": "string",
      "description": "The most recent funding round label, such as Seed, Series A, Series B, Series C, Series D, Growth, Venture Round, Angel, Convertible Note, Debt, or Grant. If not available, use 'No data'."
    },
    "lastFundingAmount": {
      "type": "string",
      "description": "Total amount raised in the most recent round as a currency string with symbol, e.g., $45,000,000. Report as given in source, or 'No data' if not available."
    },
    "lastFundingDate": {
      "type": "string",
      "description": "The month and year of funding announcement in mm-yyyy format. Example: 11-2025. If not available, use 'No data'."
    }
  },
  "required": [
    "lastFundingType",
    "lastFundingAmount",
    "lastFundingDate"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
