"{
  "type": "object",
  "properties": {
    "isUsPrimaryLocation": {
      "type": "string",
      "description": "Indicates if USA is the primary location of the company",
      "enum": [
        "YES",
        "NO",
        "N/A"
      ]
    },
    "atLeast50PeopleInUsa": {
      "type": "string",
      "description": "Indicates if there are at least 50 people in the USA",
      "enum": [
        "YES",
        "NO",
        "N/A"
      ]
    }
  },
  "required": [
    "isUsPrimaryLocation",
    "atLeast50PeopleInUsa"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "scrapedAt": {
      "type": "string",
      "description": "The date when the data was scraped, in YYYY-MM-DD format"
    },
    "cultureArtifacts": {
      "type": "array",
      "description": "Raw snippets about company values and team vibe",
      "items": {
        "type": "string"
      }
    },
    "locationData": {
      "type": "object",
      "properties": {
        "citiesMentionedRaw": {
          "type": "array",
          "description": "List of cities mentioned as office locations in raw text",
          "items": {
            "type": "string"
          }
        },
        "hubEvidence": {
          "type": "object",
          "properties": {
            "nycMentions": {
              "type": "array",
              "description": "List of addresses or snippets about the company's presence or hub in New York City",
              "items": {
                "type": "string"
              }
            },
            "sfbaMentions": {
              "type": "array",
              "description": "List of addresses or snippets about the company's presence or hub in San Francisco Bay Area",
              "items": {
                "type": "string"
              }
            }
          },
          "required": [
            "nycMentions",
            "sfbaMentions"
          ],
          "additionalProperties": false
        }
      },
      "required": [
        "citiesMentionedRaw",
        "hubEvidence"
      ],
      "additionalProperties": false
    },
    "workModelRaw": {
      "type": "array",
      "description": "Raw snippets, tags, and job title examples that reference the work model",
      "items": {
        "type": "string"
      }
    },
    "benefitsData": {
      "type": "object",
      "properties": {
        "rawBenefitsList": {
          "type": "array",
          "description": "Raw text lines or items listing employee benefits",
          "items": {
            "type": "string"
          }
        },
        "wellnessCompetitorsDetected": {
          "type": "array",
          "description": "Names of wellness competitors detected in benefits or null if none",
          "items": {
            "type": "string"
          }
        },
        "physicalAmenitiesDetected": {
          "type": "array",
          "description": "Perks that imply a physical office, such as lunch, gym, stocked kitchen, etc.",
          "items": {
            "type": "string"
          }
        }
      },
      "required": [
        "rawBenefitsList",
        "wellnessCompetitorsDetected",
        "physicalAmenitiesDetected"
      ],
      "additionalProperties": false
    },
    "jobLocationRatio": {
      "type": "object",
      "properties": {
        "remoteJobsCount": {
          "type": "integer",
          "description": "Count of jobs listed as remote"
        },
        "officeJobsCount": {
          "type": "integer",
          "description": "Count of jobs listed as based in an office or specific location"
        },
        "locationsMentioned": {
          "type": "array",
          "description": "List of all city/location names mentioned in job postings",
          "items": {
            "type": "string"
          }
        }
      },
      "required": [
        "remoteJobsCount",
        "officeJobsCount",
        "locationsMentioned"
      ],
      "additionalProperties": false
    }
  },
  "required": [
    "scrapedAt",
    "cultureArtifacts",
    "locationData",
    "workModelRaw",
    "benefitsData",
    "jobLocationRatio"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "Tier": {
      "type": "string",
      "description": "Qualification Tier assigned to the company based on ModHealth profiling.",
      "enum": [
        "1",
        "2",
        "3",
        "Disqualified"
      ]
    },
    "tierReasoning": {
      "type": "string",
      "description": "Strict 1-sentence verdict explaining the reason for the assigned Tier."
    },
    "executiveSummary": {
      "type": "object",
      "properties": {
        "benefitsScore": {
          "type": "integer",
          "description": "Score (0-5) indicating the depth and quality of employee benefits offered."
        },
        "benefitsScoreReasoning": {
          "type": "string",
          "description": "Explanation of why the given benefits score was assigned."
        },
        "benefitsClassification": {
          "type": "string",
          "description": "Benefits Classification - either Basic Benefits, Premium Benefits, Ultra-premium Benefits, or Unknown"
        },
        "benefitsExtractedRawList": {
          "type": "string",
          "description": "Comma-separated list of all benefits and perks found in job descriptions and company materials."
        },
        "wellnessCompetitors": {
          "type": [
            "string",
            "null"
          ],
          "description": "Comma-separated list of wellness competitors mentioned, or null if none found."
        },
        "wellnessValidationSignals": {
          "type": [
            "string",
            "null"
          ],
          "description": "Comma-separated list of validation signals (e.g. Lyra, Spring Health) found in company materials, or null."
        },
        "cultureType": {
          "type": "string",
          "description": "Type of corporate culture detected at the company.",
          "enum": [
            "Goal-Oriented",
            "People-Oriented",
            "Mix",
            "Unknown"
          ]
        },
        "cultureReasoning": {
          "type": "string",
          "description": "One sentence explaining the reasoning for assigning the culture type."
        },
        "workModel": {
          "type": "string",
          "description": "Observed or inferred work model based on company disclosures and in-office perk signals.",
          "enum": [
            "Hybrid",
            "In-Office",
            "Fully Remote",
            "Unknown"
          ]
        },
        "workModelReasoning": {
          "type": "string",
          "description": "Explanation of how the work model was assigned or inferred."
        },
        "officeLocations": {
          "type": "array",
          "description": "List of city and state pairs where the company operates physical offices.",
          "items": {
            "type": "string"
          }
        }
      },
      "required": [
        "benefitsScore",
        "benefitsScoreReasoning",
        "benefitsExtractedRawList",
        "wellnessCompetitors",
        "wellnessValidationSignals",
        "cultureType",
        "cultureReasoning",
        "workModel",
        "workModelReasoning",
        "officeLocations"
      ],
      "additionalProperties": false
    }
  },
  "required": [
    "Tier",
    "tierReasoning",
    "executiveSummary"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "companyVertical": {
      "type": "string",
      "description": "Selected company vertical according to the HubSpot picker values"
    },
    "country": {
      "type": "string",
      "description": "Country name of the primary company location"
    },
    "city": {
      "type": "string",
      "description": "City name of the primary company location"
    },
    "state": {
      "type": "string",
      "description": "State name of the primary company location"
    },
    "industry": {
      "type": "string",
      "description": "Selected industry according to the HubSpot ENUM values"
    },
    "officeLocation": {
      "type": "string",
      "description": "Semi-colon separated list of mapped office locations per HubSpot picker options"
    },
    "lastFundingType": {
      "type": "string",
      "description": "Most recent funding type: Pre-seed, Seed, Series A, Series B, Series B+, or Unknown"
    },
    "lastFundingDate": {
      "type": [
        "integer",
        "null"
      ],
      "description": "The last funding date of the company as a Unix timestamp in milliseconds. Return null if the date is not found."
    },
    "sfEmployeeCount": {
      "type": [
        "integer",
        "null"
      ],
      "description": "Number of employees in the San Francisco Bay Area, or null if not found"
    },
    "nyEmployeeCount": {
      "type": [
        "integer",
        "null"
      ],
      "description": "Number of employees in the New York City Metropolitan Area, or null if not found"
    },
    "cultureResearchSummary": {
      "type": "string",
      "description": "Brief 1-2 sentence summary for personalization based on company culture research"
    },
    "cultureNewsLinks": {
      "type": "string",
      "description": "Semicolon-separated URLs to relevant culture news or articles"
    },
    "notableBenefits": {
      "type": "string",
      "description": "Comma or semicolon separated list of premium or ultra-premium employee benefits, or 'None' if not found"
    }
  },
  "required": [
    "companyVertical",
    "country",
    "city",
    "state",
    "industry",
    "officeLocation",
    "lastFundingType",
    "lastFundingDate",
    "sfEmployeeCount",
    "nyEmployeeCount",
    "cultureResearchSummary",
    "cultureNewsLinks",
    "notableBenefits"
  ],
  "additionalProperties": false
}"

