formulaMap.jsonSchema:
"{
  "type": "object",
  "properties": {
    "hasTrigger": {
      "type": "boolean",
      "description": "Whether any buying trigger was found"
    },
    "triggerType": {
      "type": "string",
      "description": "Primary trigger type",
      "enum": [
        "Major Product Release",
        "Company News",
        "Culture & Wellness",
        "None"
      ]
    },
    "summary": {
      "type": "string",
      "description": "1-2 sentence summary of the buying trigger and why it matters for workplace wellness outreach. If none, explain briefly."
    },
    "confidence": {
      "type": "string",
      "description": "How confident this is a real buying trigger",
      "enum": [
        "High",
        "Medium",
        "Low",
        "N/A"
      ]
    }
  },
  "required": [
    "hasTrigger",
    "triggerType",
    "summary",
    "confidence"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "recommendedContactName": {
      "type": "string",
      "description": "Full name of the recommended contact"
    },
    "recommendedContactEmail": {
      "type": [
        "string",
        "null"
      ],
      "description": "Email if available, null otherwise"
    },
    "recommendedContactTitle": {
      "type": "string",
      "description": "Job title of recommended contact, or ideal role to find if none exist"
    },
    "whyThisContact": {
      "type": "string",
      "description": "1 sentence on why this person is the best target for this trigger"
    },
    "outreachAngle": {
      "type": "string",
      "description": "2-3 sentence outreach angle connecting the buying trigger to Mos Health value"
    },
    "noIdealContact": {
      "type": "boolean",
      "description": "True if no ideal contact exists in HubSpot for this trigger"
    }
  },
  "required": [
    "recommendedContactName",
    "recommendedContactEmail",
    "recommendedContactTitle",
    "whyThisContact",
    "outreachAngle",
    "noIdealContact"
  ],
  "additionalProperties": false
}"
