"{
  "type": "object",
  "properties": {
    "linkedinCompanyUrl": {
      "type": "string",
      "description": "The validated LinkedIn company page URL in the format https://www.linkedin.com/company/<slug>, or an empty string if no valid company page exists."
    },
    "companyOverview": {
      "type": "string",
      "description": "A single paragraph of 5–6 sentences summarizing the company based on its official website or, if not available, other reliable public sources. If no reliable information is found, this should be an empty string."
    }
  },
  "required": [
    "linkedinCompanyUrl",
    "companyOverview"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "companyName": {
      "type": "string",
      "description": "The name of the company being qualified."
    },
    "isB2B": {
      "type": "string",
      "description": "Indicates if the company operates with a B2B business model.",
      "enum": [
        "yes",
        "no"
      ]
    },
    "commercialReadiness": {
      "type": "string",
      "description": "Indicates if the company is ready to sell a solution or still in R&D/clinical stage.",
      "enum": [
        "Ready (Solution/Service)",
        "Not Ready (R&D/Clinical)"
      ]
    },
    "targetPersonaFit": {
      "type": "string",
      "description": "Indicates how well the company's buyer persona fits Backpack AI's LinkedIn signal tracking.",
      "enum": [
        "High (LinkedIn Active)",
        "Low (Offline/Blue Collar)"
      ]
    },
    "qualificationStatus": {
      "type": "string",
      "description": "The final qualification response for the company.",
      "enum": [
        "Qualified",
        "Disqualified"
      ]
    },
    "disqualificationReason": {
      "type": [
        "string",
        "null"
      ],
      "description": "The reason for disqualification if company is disqualified, otherwise null.",
      "enum": [
        "Competitor",
        "B2C",
        "R&D Mode - Not ready for outbound",
        "Persona not on LinkedIn",
        null
      ]
    },
    "reasoning": {
      "type": "string",
      "description": "A concise sentence explaining the qualification or disqualification decision."
    }
  },
  "required": [
    "companyName",
    "isB2B",
    "commercialReadiness",
    "targetPersonaFit",
    "qualificationStatus",
    "disqualificationReason",
    "reasoning"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "website": {
      "type": [
        "string",
        "null"
      ],
      "description": "The official website URL of the company or null if not found"
    },
    "linkedinUrl": {
      "type": [
        "string",
        "null"
      ],
      "description": "The LinkedIn profile URL of the company or null if not found"
    }
  },
  "required": [
    "website",
    "linkedinUrl"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "reason": {
      "type": "string",
      "description": "Explanation for why no website was found"
    },
    "website": {
      "type": [
        "string",
        "null"
      ],
      "description": "The correct website for the company if identified, otherwise null"
    },
    "linkedinUrl": {
      "type": [
        "string",
        "null"
      ],
      "description": "The LinkedIn URL for the company if identified, otherwise null"
    }
  },
  "required": [
    "reason",
    "website",
    "linkedinUrl"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "qualificationStatus": {
      "type": "string",
      "description": "Indicates if the company is Qualified or Disqualified based on the Kill Chain exclusion check.",
      "enum": [
        "Qualified",
        "Disqualified"
      ]
    },
    "qualificationReasoning": {
      "type": "string",
      "description": "1-2 sentences explaining the qualification status. If Disqualified, specify which Kill Chain rule was triggered."
    },
    "classification": {
      "type": "object",
      "properties": {
        "businessModel": {
          "type": "string",
          "description": "The type of business model the company operates under."
        },
        "companyType": {
          "type": "string",
          "description": "The general category or type of company (e.g., SaaS, Agency, Hardware, etc)."
        },
        "productDescription": {
          "type": "string",
          "description": "Two sentences focusing on the real functional utility of the product, avoiding marketing language."
        },
        "targetMarketSegment": {
          "type": "object",
          "properties": {
            "primaryFocus": {
              "type": "string",
              "description": "The main segment the company targets.",
              "enum": [
                "SMB",
                "Mid-Market",
                "Enterprise",
                "unknown"
              ]
            },
            "fullDescription": {
              "type": "string",
              "description": "A full sentence describing the market segments targeted by the company."
            }
          },
          "required": [
            "primaryFocus",
            "fullDescription"
          ],
          "additionalProperties": false
        },
        "targetIndustry": {
          "type": "object",
          "properties": {
            "primaryFocus": {
              "type": "string",
              "description": "The primary industry the company serves."
            },
            "secondaryTargets": {
              "type": "array",
              "description": "Other secondary industries the company targets.",
              "items": {
                "type": "string"
              }
            },
            "fullDescription": {
              "type": "string",
              "description": "A full description of the industries the company focuses on."
            }
          },
          "required": [
            "primaryFocus",
            "secondaryTargets",
            "fullDescription"
          ],
          "additionalProperties": false
        },
        "targetPersona": {
          "type": "string",
          "description": "A concise statement of the main roles or personas the company targets as customers."
        },
        "idealCustomerProfile": {
          "type": "string",
          "description": "The ideal_customer_profile narrative template filled per instructions."
        },
        "buyerPersona": {
          "type": "string",
          "description": "The buyer_persona narrative template filled per instructions."
        },
        "teamsTheySellToDropdown": {
          "type": "array",
          "description": "List of teams the company sells to, using values from the provided knowledge base.",
          "items": {
            "type": "string"
          }
        },
        "teamsTheySellToDescription": {
          "type": "string",
          "description": "A description of the teams the company sells to."
        },
        "linkedinOutboundFit": {
          "type": "string",
          "description": "Fit level based on the provided role list for LinkedIn outbound, as specified in instructions.",
          "enum": [
            "strong_yes",
            "uncertain",
            "no"
          ]
        },
        "linkedinOutboundFitReason": {
          "type": "string",
          "description": "An explanation of the outbound fit classification, referencing the specific role from the Strong Fit Role List."
        }
      },
      "required": [
        "businessModel",
        "companyType",
        "productDescription",
        "targetMarketSegment",
        "targetIndustry",
        "targetPersona",
        "idealCustomerProfile",
        "buyerPersona",
        "teamsTheySellToDropdown",
        "teamsTheySellToDescription",
        "linkedinOutboundFit",
        "linkedinOutboundFitReason"
      ],
      "additionalProperties": false
    }
  },
  "required": [
    "qualificationStatus",
    "qualificationReasoning",
    "classification"
  ],
  "additionalProperties": false
}"
