"{
  "type": "object",
  "properties": {
    "linkedinCompanyUrl": {
      "type": "string",
      "description": "The validated LinkedIn company page URL in the format https://www.linkedin.com/company/<slug>, or an empty string if no valid company page exists."
    },
    "companyOverview": {
      "type": "string",
      "description": "A single paragraph of 5–6 sentences summarizing the company based on its official website or, if not available, other reliable public sources. If no reliable information is found, this should be an empty string."
    }
  },
  "required": [
    "linkedinCompanyUrl",
    "companyOverview"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "companyName": {
      "type": "string",
      "description": "The name of the company being qualified."
    },
    "isB2B": {
      "type": "string",
      "description": "Indicates if the company operates with a B2B business model.",
      "enum": [
        "yes",
        "no"
      ]
    },
    "commercialReadiness": {
      "type": "string",
      "description": "Indicates if the company is ready to sell a solution or still in R&D/clinical stage.",
      "enum": [
        "Ready (Solution/Service)",
        "Not Ready (R&D/Clinical)"
      ]
    },
    "targetPersonaFit": {
      "type": "string",
      "description": "Indicates how well the company's buyer persona fits Backpack AI's LinkedIn signal tracking.",
      "enum": [
        "High (LinkedIn Active)",
        "Low (Offline/Blue Collar)"
      ]
    },
    "qualificationStatus": {
      "type": "string",
      "description": "The final qualification response for the company.",
      "enum": [
        "Qualified",
        "Disqualified"
      ]
    },
    "disqualificationReason": {
      "type": [
        "string",
        "null"
      ],
      "description": "The reason for disqualification if company is disqualified, otherwise null.",
      "enum": [
        "Competitor",
        "B2C",
        "R&D Mode - Not ready for outbound",
        "Persona not on LinkedIn",
        null
      ]
    },
    "reasoning": {
      "type": "string",
      "description": "A concise sentence explaining the qualification or disqualification decision."
    }
  },
  "required": [
    "companyName",
    "isB2B",
    "commercialReadiness",
    "targetPersonaFit",
    "qualificationStatus",
    "disqualificationReason",
    "reasoning"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "oneLiner": {
      "type": "string",
      "description": "Identify the company's one-liner that briefly describes what they do."
    },
    "model": {
      "type": "string",
      "description": "Determine the company's business model such as B2B SaaS, B2C, or Marketplace."
    },
    "primarySalesChannel": {
      "type": "string",
      "description": "Identify the company's primary sales channel, such as Digital/LinkedIn or Field/Consumer."
    },
    "fitScore": {
      "type": "string",
      "description": "Assign a fit score of P1, P2, or P3 based on the qualification criteria.",
      "enum": [
        "P1",
        "P2",
        "P3"
      ]
    },
    "why": {
      "type": "string",
      "description": "Provide a one-sentence explanation for the assigned fit score."
    },
    "caseStudyReference": {
      "type": "string",
      "description": "If fitScore is P1, identify the most relevant case study industry and company. If P2 or P3, write N/A."
    }
  },
  "required": [
    "oneLiner",
    "model",
    "primarySalesChannel",
    "fitScore",
    "why",
    "caseStudyReference"
  ],
  "additionalProperties": false
}"

"{
  "type": "object",
  "properties": {
    "qualificationStatus": {
      "type": "string",
      "description": "Indicates whether the company is Qualified or Disqualified based on exclusion checks.",
      "enum": [
        "Qualified",
        "Disqualified"
      ]
    },
    "qualificationReasoning": {
      "type": "string",
      "description": "A brief explanation (1-2 sentences) for the qualification status. If Disqualified, explain which Kill Chain rule was triggered."
    },
    "classification": {
      "type": "object",
      "properties": {
        "businessModel": {
          "type": "string",
          "description": "The business model of the company."
        },
        "companyType": {
          "type": "string",
          "description": "The type of company, such as SaaS, Hybrid, Agency, etc."
        },
        "productDescription": {
          "type": "string",
          "description": "A two-sentence description of the product, focusing on its function, using verbs and avoiding marketing fluff."
        },
        "targetMarketSegment": {
          "type": "object",
          "properties": {
            "primaryFocus": {
              "type": "string",
              "description": "The primary market segment focus.",
              "enum": [
                "SMB",
                "Mid-Market",
                "Enterprise",
                "unknown"
              ]
            },
            "fullDescription": {
              "type": "string",
              "description": "Full description of the market segment focus."
            }
          },
          "required": [
            "primaryFocus",
            "fullDescription"
          ],
          "additionalProperties": false
        },
        "targetIndustry": {
          "type": "object",
          "properties": {
            "primaryFocus": {
              "type": "string",
              "description": "The primary industry the company targets."
            },
            "secondaryTargets": {
              "type": "array",
              "description": "List of secondary industries the company targets.",
              "items": {
                "type": "string"
              }
            },
            "fullDescription": {
              "type": "string",
              "description": "Full description of target industry coverage."
            }
          },
          "required": [
            "primaryFocus",
            "secondaryTargets",
            "fullDescription"
          ],
          "additionalProperties": false
        },
        "targetPersona": {
          "type": "string",
          "description": "A description of the roles or personas the company targets."
        },
        "idealCustomerProfile": {
          "type": "string",
          "description": "The ideal customer profile completed using the specified template from the knowledge base."
        },
        "buyerPersona": {
          "type": "string",
          "description": "The buyer persona completed using the specified template from the knowledge base."
        },
        "teamsTheySellToDropdown": {
          "type": "array",
          "description": "Dropdown list of team names they sell to, based on the knowledge base values.",
          "items": {
            "type": "string"
          }
        },
        "teamsTheySellToDescription": {
          "type": "string",
          "description": "A plain sentence that describes the teams they sell to."
        },
        "linkedinOutboundFit": {
          "type": "string",
          "description": "Indicates outbound fit: strong_yes, uncertain, or no.",
          "enum": [
            "strong_yes",
            "uncertain",
            "no"
          ]
        },
        "linkedinOutboundFitReason": {
          "type": "string",
          "description": "Explanation of the match (or not) against the Strong Fit Role List."
        }
      },
      "required": [
        "businessModel",
        "companyType",
        "productDescription",
        "targetMarketSegment",
        "targetIndustry",
        "targetPersona",
        "idealCustomerProfile",
        "buyerPersona",
        "teamsTheySellToDropdown",
        "teamsTheySellToDescription",
        "linkedinOutboundFit",
        "linkedinOutboundFitReason"
      ],
      "additionalProperties": false
    }
  },
  "required": [
    "qualificationStatus",
    "qualificationReasoning",
    "classification"
  ],
  "additionalProperties": false
}"
